mapper.select:
SELECT COUNT(*) 
  FROM F_STATEMENT
 WHERE memid = #{memid}

SELECT COUNT(*)
  FROM F_STATEMENT
 WHERE cid = #{cid}

mapper.delete:
DELETE FROM F_STATEMENT
WHERE cid = #{cid}
  AND memid = #{memid}
  AND SYSDATE < ( SELECT start_date
  					   FROM F_CLASS
  					  WHERE cid = #{cid} )
